con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info "Hello"
log.info context.expand('${#TestCase#name}')
log.info testRunner.testCase.testSuite.testCases["getEmployeeDetails"].getPropertyValue("id")
testRunner.testCase.testSuite.testCases["getEmployeeDetails"].setPropertyValue("id","456")
log.info testRunner.testCase.getPropertyValue("age")
log.info testRunner.testCase.testSuite.testCases["getEmployeeDetails"].testSteps["get"].getPropertyValue("Request")

def project=testRunner.testCase.testSuite.project
log.info project.getPropertyValue("gender")

def addReq=testRunner.testCase.testSuite.testCases["addEmployee"].testSteps["add"].getPropertyValue("Request")
def xmlAdd=new XmlHolder(addReq)
def name=testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("name")
def age=testRunner.testCase.getPropertyValue("age")
def id=testRunner.testCase.testSuite.testCases["getEmployeeDetails"].getPropertyValue("id")

xmlAdd.setNodeValue("//typ:addEmployee/typ:name",name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id",id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:age",age)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department","eecs")

def newXmlAdd=xmlAdd.getXml() //save new xml
testRunner.testCase.testSuite.testCases["addEmployee"].testSteps["add"].setPropertyValue("Request",newXmlAdd)
def addTestStep=testRunner.testCase.testSuite.testCases["addEmployee"].testSteps["add"]
def contextAddEmployee=new WsdlTestRunContext(addTestStep)
addTestStep.run(testRunner, contextAddEmployee)

def getReq=testRunner.testCase.testSuite.testCases["getEmployeeDetails"].testSteps["get"].getPropertyValue("Request")
def xmlGetEmp=new XmlHolder(getReq)
xmlGetEmp.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name)
def newXmlGetEmp=xmlGetEmp.getXml()
testRunner.testCase.testSuite.testCases["getEmployeeDetails"].testSteps["get"].setPropertyValue("Request",newXmlGetEmp)
def getTestStep=testRunner.testCase.testSuite.testCases["getEmployeeDetails"].testSteps["get"]
def contextGetEmployee=new WsdlTestRunContext(getTestStep)
getTestStep.run(testRunner, contextGetEmployee)

//Validation
def getResp=testRunner.testCase.testSuite.testCases["getEmployeeDetails"].testSteps["get"].getPropertyValue("Response")
def xmlGetResp=new XmlHolder(getResp)
getRespName=xmlGetResp.getNodeValue("//ns:getEmployeeDetailsResponse/ns:return/ns:name")
log.info getRespName
log.info name
assert getRespName==name
